CREATE TABLE TREINA_CONVENIADAS (ID_TREINA_CONVENIADA INT IDENTITY, CONVENIADA CHAR(06) NOT NULL PRIMARY KEY, DESCRICAO VARCHAR(20) NOT NULL, USUARIO_ATUALIZACAO CHAR(10) NOT NULL, DATA_ATUALIZACAO DATETIME NOT NULL) 


CREATE TABLE TREINA_USUARIOS (ID_TREINA_USUARIO INT IDENTITY, USUARIO CHAR(10) NOT NULL PRIMARY KEY, SENHA CHAR (10) NOT NULL, NOME VARCHAR(20) NOT NULL,USUARIO_ATUALIZACAO CHAR(10) NOT NULL, DATA_ATUALIZACAO DATETIME NOT NULL) 


CREATE TABLE TREINA_SITUACAO (ID_TREINA_SITUACAO INT IDENTITY, SITUACAO CHAR(02) NOT NULL PRIMARY KEY, DESCRICAO VARCHAR(25) NOT NULL, USUARIO_ATUALIZACAO CHAR(10) NOT NULL, DATA_ATUALIZACAO DATETIME NOT NULL) 


CREATE TABLE TREINA_PROPOSTAS (ID_TREINA_PROPOSTA INT IDENTITY, PROPOSTA NUMERIC(10) NOT NULL PRIMARY KEY, CPF CHAR(11) NOT NULL, CONVENIADA CHAR(06) FOREIGN KEY REFERENCES TREINA_CONVENIADAS(CONVENIADA) , 
VLR_SOLICITADO NUMERIC (12,2), PRAZO NUMERIC (3), VLR_FINANCIADO NUMERIC (12,2), SITUACAO CHAR(02) FOREIGN KEY REFERENCES TREINA_SITUACAO(SITUACAO) NOT NULL, OBSERVACAO VARCHAR(500) NULL,
DT_SITUACAO SMALLDATETIME NOT NULL, USUARIO CHAR(10) FOREIGN KEY REFERENCES TREINA_USUARIOS(USUARIO) NOT NULL, USUARIO_ATUALIZACAO CHAR(10) NOT NULL, DATA_ATUALIZACAO DATETIME NOT NULL) 


CREATE TABLE TREINA_CLIENTES (ID_TREINA_CLIENTE INT IDENTITY, CPF CHAR(11) NOT NULL PRIMARY KEY, NOME VARCHAR (60) NOT NULL, DT_NASCIMENTO smalldatetime NOT NULL, GENERO CHAR(01) NOT NULL, VLR_SALARIO NUMERIC(12,2) NOT NULL, LOGRADOURO VARCHAR(60) NOT NULL, NUMERO_RESIDENCIA CHAR(10) NOT NULL, BAIRRO VARCHAR(60) NOT NULL, CIDADE VARCHAR(60) NOT NULL, CEP CHAR(08) NOT NULL, USUARIO_ATUALIZACAO CHAR(10) NOT NULL, DATA_ATUALIZACAO DATETIME NOT NULL)


CREATE TABLE TREINA_LIMITES_IDADE_CONVENIADA (ID_TREINA_LIM_IDADE_CONVENIADA INT IDENTITY, CONVENIADA CHAR(06) FOREIGN KEY REFERENCES TREINA_CONVENIADAS(CONVENIADA), IDADE_INICIAL NUMERIC (3) NOT NULL, IDADE_FINAL NUMERIC (3) NOT NULL, VALOR_LIMITE NUMERIC(12,2) NOT NULL, PERCENTUAL_MAXIMO_ANALISE NUMERIC (3) NOT NULL, USUARIO_ATUALIZACAO CHAR(10) NOT NULL, DATA_ATUALIZACAO DATETIME NOT NULL) 



